#find and replace "hostname\username" and "hostname\Users" - changing "hostname" to your target host and "username" to your target user
Get-ChildItem -Directory -Recurse -Depth 1 -Force  C:\ -ErrorAction SilentlyContinue | where {(get-acl $_.fullname).access | where {($_.identityreference -eq 'hostname\username') -or ($_.identityreference -eq 'nt authority\authenticated users') -or ($_.identityreference -eq 'builtin\users') -or ($_.identityreference -eq 'hostname\Users') -and ($_.filesystemrights -eq 'FullControl') -or ($_.filesystemrights -eq 'Modify') -or ($_.filesystemrights -eq 'Write')}}
